// ดึงข้อมูลการเตรียมคำสั่งสอบถาม
const queryData = $('Prepare Gate Query').first().json;
const records = $input.all();

// สร้างข้อมูลจัดกลุ่ม
const grouped = {};
queryData.cameraIds.forEach(cid => {
  grouped[cid] = {
    gate_id: queryData.gateId,
    camera_id: cid,
    start: queryData.start,
    stop: queryData.stop,
    details: {}
  };

  // กำหนดค่าทั้งหมดเป็น 0
  queryData.vehicleTypeIds.forEach(vtid => {
    queryData.directionTypeIds.forEach(dtid => {
      const key = `${vtid}-${dtid}`;
      grouped[cid].details[key] = {
        vehicle_type_id: vtid,
        vehicle_type_name: queryData.vehicleTypeMap[vtid],
        direction_type_id: dtid,
        direction_type_name: queryData.directionTypeMap[dtid],
        count: 0
      };
    });
  });
});

// เติมข้อมูลจริงจากระเบียน
records.forEach(record => {
  const row = record.json;
  const cid = row.camera_id;
  const vtid = row.vehicle_type_id;
  const dtid = row.direction_type_id;
  const key = `${vtid}-${dtid}`;

  if (grouped[cid] && grouped[cid].details[key]) {
    grouped[cid].details[key].count = parseInt(row.count);
  }
});

// แปลงเป็นรูปแบบสุดท้าย
const data = [];
Object.values(grouped).forEach(cam => {
  cam.details = Object.values(cam.details);
  data.push(cam);
});

return { data: data };

// ดึงข้อมูลพารามิเตอร์ (จาก Node ที่เตรียมข้อมูล)
const queryData = $('Prepare Camera Query').first().json;  // หรือใช้ชื่อ Node ที่ถูกต้องจริง

const records = $input.all();

// สร้างรายละเอียดด้วยค่าทั้งหมด
const details = {};
queryData.vehicleTypeIds.forEach(vtid => {
  queryData.directionTypeIds.forEach(dtid => {
    const key = `${vtid}-${dtid}`;
    details[key] = {
      vehicle_type_id: vtid,
      vehicle_type_name: queryData.vehicleTypeMap[vtid],
      direction_type_id: dtid,
      direction_type_name: queryData.directionTypeMap[dtid],
      count: 0
    };
  });
});

// เติมข้อมูลจริงจากระเบียน
records.forEach(record => {
  const row = record.json;
  const vtid = row.vehicle_type_id;
  const dtid = row.direction_type_id;
  const key = `${vtid}-${dtid}`;

  if (details[key]) {
    details[key].count = parseInt(row.count);
  }
});

// สร้างการตอบสนองสุดท้าย
const data = [{
  gate_id: queryData.gateId,
  camera_id: queryData.cameraId,
  start: queryData.start,
  stop: queryData.stop,
  details: Object.values(details)
}];

return { data };

// ดึงข้อมูลจากโหนดก่อนหน้า
const params = $('Combine Types Data').first().json;
const gateData = $('Get Gate ID').first().json;

// แปลงวันที่เป็น TIMESTAMP
const startDateTime = params.start + ' 00:00:00';
const stopDateTime = params.stop + ' 23:59:59';

// สร้างคำสั่ง SQL สำหรับกล้อง
const sql = `SELECT vehicle_type_id, direction_type_id, SUM(count) as count
             FROM DetectionRecord
             WHERE camera_id = ${params.id} AND time BETWEEN '${startDateTime}' AND '${stopDateTime}'
             GROUP BY vehicle_type_id, direction_type_id`;

return {
  sql,
  vehicleTypeMap: params.vehicleTypeMap,
  directionTypeMap: params.directionTypeMap,
  vehicleTypeIds: params.vehicleTypeIds,
  directionTypeIds: params.directionTypeIds,
  cameraId: params.id,
  gateId: gateData.gate_id,
  start: params.start,
  stop: params.stop
};

// ดึงข้อมูลจากโหนดก่อนหน้า
const combinedData = $('Combine Types Data').first().json;
const cameras = $('Get Cameras in Gate').all();

// ดึงรหัสกล้องทั้งหมดใน gate นั้น
const cameraIds = cameras.map(c => c.json.id);

// ถ้าไม่พบกล้องใดเลย
if (cameraIds.length === 0) {
  return [{ json: { data: [] } }];
}

// เตรียมค่าเริ่มและสิ้นสุดเวลา
const start = combinedData.start + ' 00:00:00';
const stop = combinedData.stop + ' 23:59:59';

// สร้างคำสั่ง SQL แบบแปะค่าลงไปตรง ๆ
const sql = `SELECT camera_id, vehicle_type_id, direction_type_id, SUM(count) as count
             FROM DetectionRecord
             WHERE camera_id IN (${cameraIds.join(',')}) AND time BETWEEN '${start}' AND '${stop}'
             GROUP BY camera_id, vehicle_type_id, direction_type_id`;

// ส่งข้อมูลที่ต้องใช้ต่อไปยัง Node ถัดไป
return {
  sql: sql,
  vehicleTypeMap: combinedData.vehicleTypeMap,
  directionTypeMap: combinedData.directionTypeMap,
  vehicleTypeIds: combinedData.vehicleTypeIds,
  directionTypeIds: combinedData.directionTypeIds,
  cameraIds: cameraIds,
  gateId: combinedData.id,
  start: combinedData.start,
  stop: combinedData.stop
};

// รับข้อมูล input หลัก
const inputData = $input.first();
const inputJson = inputData?.json ?? {};

// เตรียม Map
const vehicleTypeMap = {};
const vehicleTypeIds = [];
const directionTypeMap = {};
const directionTypeIds = [];

// รับ VehicleTypes
if ($('Get VehicleTypes')) {
  $('Get VehicleTypes').all().forEach(vt => {
    if (!(vt.json.id in vehicleTypeMap)) {
      vehicleTypeMap[vt.json.id] = vt.json.name;
      vehicleTypeIds.push(vt.json.id);
    }
  });
}

// รับ DirectionTypes
if ($('Get DirectionTypes')) {
  $('Get DirectionTypes').all().forEach(dt => {
    if (!(dt.json.id in directionTypeMap)) {
      directionTypeMap[dt.json.id] = dt.json.name;
      directionTypeIds.push(dt.json.id);
    }
  });
}

// คืนค่าครบ
return {
  ...inputJson,
  vehicleTypeMap,
  directionTypeMap,
  vehicleTypeIds,
  directionTypeIds,
};

// แยกพารามิเตอร์จาก URL
const uri = $input.first().json.params.path;
const fullUrl = $input.first().json.headers.referer || $input.first().json.query;

// ดึงค่าพารามิเตอร์จาก query string
const queryParams = $input.first().json.query;
const type = queryParams.type; // 'gate' หรือ 'camera'
const id = parseInt(queryParams.id);
const start = queryParams.start;
const stop = queryParams.stop;

// ตรวจสอบความถูกต้องของพารามิเตอร์
if (!type || !id || !start || !stop) {
  throw new Error('พารามิเตอร์ไม่ถูกต้อง ต้องระบุ: type, id, start, stop');
}

if (type !== 'gate' && type !== 'camera') {
  throw new Error('type ต้องเป็น "gate" หรือ "camera" เท่านั้น');
}
if (!['gate', 'camera'].includes(type)) {
  throw new Error('type ต้องเป็น "gate" หรือ "camera" เท่านั้น');
}
if (isNaN(id)) {
  throw new Error('id ต้องเป็นตัวเลข');
}

return {
  type: type,
  id: id,
  start: start + ' 00:00:00',
  stop: stop + ' 23:59:59',
};
